package com.spandiar.spring;

public class Triangle {
	
	private PointCoordinates pointA;
	private PointCoordinates pointB;
	private PointCoordinates pointC;	

	public Triangle() {
		// TODO Auto-generated constructor stub
	}

	public PointCoordinates getPointA() {
		return pointA;
	}

	public void setPointA(PointCoordinates pointA) {
		this.pointA = pointA;
	}

	public PointCoordinates getPointB() {
		return pointB;
	}

	public void setPointB(PointCoordinates pointB) {
		this.pointB = pointB;
	}

	public PointCoordinates getPointC() {
		return pointC;
	}

	public void setPointC(PointCoordinates pointC) {
		this.pointC = pointC;
	}
	
}
